document.addEventListener("DOMContentLoaded", function() {
  const widget = document.getElementById('countdown-widget');
  const titleEl = widget.querySelector('.cd-title');
  const applyBtn = widget.querySelector('#cd-apply-btn');
  const remainingText = widget.querySelector('#cd-remaining-text');
  const headerEl = widget.querySelector('.cd-header');
  const timerEl = widget.querySelector('.cd-timer');

  const link = widget.getAttribute('data-link');
  const title = widget.getAttribute('data-event-title');
  const btnText = widget.getAttribute('data-button');

  titleEl.textContent = title;
  if (titleEl && !titleEl.querySelector('.cd-ico')) {
    const bell = document.createElement('span');
    bell.className = 'cd-ico';
    bell.textContent = '⏰';
    titleEl.prepend(bell);
  }
  applyBtn.textContent = btnText;
  applyBtn.addEventListener('click', () => { window.location.href = link; });
  if (timerEl && !timerEl.querySelector('.cd-done')) {
    const done = document.createElement('span');
    done.className = 'cd-done';
    done.textContent = '남았습니다.';
    timerEl.appendChild(done);
  }

  const cardEl = widget.querySelector('.cd-card');
  const badge = document.createElement('span');
  badge.id = 'cd-badge';
  badge.className = 'cd-badge';
  badge.setAttribute('aria-live','polite');
  cardEl.classList.add('has-badge');
  cardEl.appendChild(badge);

  const floatCta = document.createElement('a');
  floatCta.href = link;
  floatCta.className = 'cd-float-cta';
  floatCta.setAttribute('aria-label', btnText);
  floatCta.innerHTML = '<span class="dot"></span><span class="text">' + btnText + '</span>';
  document.body.appendChild(floatCta);
  setTimeout(() => floatCta.classList.add('show'), 600);

  if ('IntersectionObserver' in window) {
    const io = new IntersectionObserver((entries) => {
      const visible = entries[0] && entries[0].isIntersecting;
      if (visible) floatCta.classList.add('is-hidden');
      else floatCta.classList.remove('is-hidden');
    }, { threshold: 0.4 });
    io.observe(widget);
  }

  const now = new Date();
  const deadline = new Date();
  deadline.setDate(now.getDate() + 1);
  deadline.setHours(18, 38, 29, 0);

  const daysEl = document.getElementById('cd-days');
  const hoursEl = document.getElementById('cd-hours');
  const minsEl = document.getElementById('cd-mins');
  const secsEl = document.getElementById('cd-secs');

  function pad(n){ return n < 10 ? '0'+n : n; }

  const BADGE_MSGS = ['읽기 전에 확인해보세요', '곧 마감이 되니 확인해보세요', '체크포인트 확인'];
  let currentSet = BADGE_MSGS;
  let msgIndex = 0;
  function setBadgeText(){
    if (!badge) return;
    badge.textContent = currentSet[msgIndex % currentSet.length];
  }
  setBadgeText();
  let blinkCount = 0;
  badge.addEventListener('animationiteration', () => {
    blinkCount++;
    if (blinkCount % 2 === 0) {
      msgIndex++;
      setBadgeText();
    }
  });


  let prevDays = null, prevHours = null, prevMins = null, prevSecs = null;

  function updateCountdown(){
    const now = new Date();
    const diff = deadline - now;

    if(diff <= 0){
      daysEl.textContent = '0';
      hoursEl.textContent = '00';
      minsEl.textContent = '00';
      secsEl.textContent = '00';
      remainingText.textContent = '마감되었습니다.';
      applyBtn.textContent = '마감됨';
      applyBtn.disabled = true;
      applyBtn.style.opacity = 0.7;
      applyBtn.style.cursor = 'not-allowed';
      clearInterval(timer);
      floatCta.classList.add('is-hidden');
      return;
    }

    const days = Math.floor(diff / (1000*60*60*24));
    const hours = Math.floor((diff / (1000*60*60)) % 24);
    const mins = Math.floor((diff / (1000*60)) % 60);
    const secs = Math.floor((diff / 1000) % 60);

    daysEl.textContent = days;
    hoursEl.textContent = pad(hours);
    minsEl.textContent = pad(mins);
    secsEl.textContent = pad(secs);

    if (prevDays !== null && prevDays !== days) { daysEl.classList.add('cd-flash'); setTimeout(()=>daysEl.classList.remove('cd-flash'), 500); }
    if (prevHours !== null && prevHours !== hours) { hoursEl.classList.add('cd-flash'); setTimeout(()=>hoursEl.classList.remove('cd-flash'), 500); }
    if (prevMins !== null && prevMins !== mins) { minsEl.classList.add('cd-flash'); setTimeout(()=>minsEl.classList.remove('cd-flash'), 500); }
    if (prevSecs !== null && prevSecs !== secs) { secsEl.classList.add('cd-flash'); setTimeout(()=>secsEl.classList.remove('cd-flash'), 500); }
    prevDays = days; prevHours = hours; prevMins = mins; prevSecs = secs;

    remainingText.textContent = `남은 시간: ${days}일 ${hours}시간 ${mins}분 ${secs}초`;
  }

  updateCountdown();
  const timer = setInterval(updateCountdown, 1000);
});
